package loopassignment;

public class looping1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for( i=1;i<=10;i++) {
			System.out.print(i);
		}
		System.out.println();
		System.out.println(i);
		System.out.println();
		 i=1;
		while(i<=10) {
			System.out.print(i);
			i++;
		}System.out.println();
		System.out.println(i);
		System.out.println();
		i=5;
		do {
			System.out.print(i);
			i++;
		}while(i<=1);System.out.println();
		System.out.println(i);
	}

}
